import React from 'react';
import Pokedex from './components/pokedex/pokedex.component';

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hands: [{
        id: 1,
        cards: [],
        score1: 0 
      },
      {
        id: 2,
        cards: [],
        score2: 0 
      }]
    }
  }

  render() {
    const { hands, ...otherAppProps } = this.state;
    return (
      <div className="App">
        {
         hands.map(hand => <Pokedex key={hand.id} {...otherAppProps} />)
        }

      </div>
    );
  }
}

export default App;
